mapper.select:
select evaluate.id,userid,evaluateinfoid,createtime,evaluateinfo.content as evaluate_content from evaluate left
join evaluateinfo on evaluate.evaluateinfoid = evaluateinfo.id
where userid=#{userId,jdbcType=VARCHAR}

select evaluate.id,userid,evaluateinfoid,createtime,evaluateinfo.content as evaluate_content from evaluate left
join evaluateinfo on evaluate.evaluateinfoid = evaluateinfo.id
where userid=#{userId,jdbcType=VARCHAR} and evaluateinfoid=#{evaluateInfoId,jdbcType=INTEGER}
